def firstMissingPositive(nums):
    """
    :type nums: List[int]
    :rtype: int

    Source: https://leetcode.com/problems/first-missing-positive/description/

    Given an unsorted integer array, find the first missing positive integer.
    """
    n = len(nums)

    # Puts elements in the array in the range(1,n) to indices which are equal to its (value - 1) as array starts at 0
    for i in range(0, n):
        while nums[i] != i + 1:
            if (nums[i] <= 0) or (nums[i] >= n):
                break
            if nums[i] == nums[nums[i] - 1]:
                break
            temp = nums[i]
            nums[i], nums[temp - 1] = nums[temp - 1], temp

    for i, num in enumerate(nums):
        # Find out the smallest element which is not in it's place
        if num != i + 1:
            return i + 1

    return n + 1


